INSERT INTO champions_skill VALUES(1,'숲의 대장간','active',160,120,replace(replace('주문력 비례 보호막: 300% / 350% / 1200%\r\n보호막: 체력의 20% + 주문력 비례 보호막\r\n피해량: 300 / 450 / 1500 (주문력 비례)','\r',char(13)),'\n',char(10)),'4초 동안 피해를 흡수하는 보호막을 얻습니다. 2초 동안 멈출 수 없는 상태로 정신 집중을 하여 재련을 하며, 재련이 완료되면 주변의 적에게 마법 피해를 입힙니다. 가장 가까운 아군에게 임시로 완성한 아이템을 장착시켜 줍니다.');
INSERT INTO champions_skill VALUES(2,'용기','active',80,30,replace(replace('보호막: 주문력의 250% / 275% / 300%\r\n피해량: 공격력의 350% / 350% / 350%','\r',char(13)),'\n',char(10)),'5초 동안 보호막을 얻습니다. 가렌의 다음 공격이 추가 피해를 입힙니다.');
INSERT INTO champions_skill VALUES(3,'화려한 등장','active',70,30,replace(replace('피해량: 주문력의 270% / 405% / 1500%\r\n보호막: 주문력의 175% / 225% / 500%','\r',char(13)),'\n',char(10)),'가장 적이 많이 모여있는 곳으로 돌진하여 주변 2칸의 적에게 마법 피해를 입힙니다. 적중시킨 적 하나당 2초 동안 보호막을 얻고, 4초 동안 적이 주는 피해량을 10%만큼 감소시킵니다.');
INSERT INTO champions_skill VALUES(4,'부러진 날개','active',25,0,replace(replace('피해량: 공격력의 150% / 150% / 155%\r\n세 번째 사용 시 피해량: 공격력의 300% / 300% / 310%\r\n체력 회복: 주문력의 150% / 180% / 220%','\r',char(13)),'\n',char(10)),'현재 대상에게 물리 피해를 입힙니다. 세 번째 시전마다 추가로 주변 적에게 물리 피해를 입히고 체력을 회복합니다.');
INSERT INTO champions_skill VALUES(5,'구원자의 보호','active',140,70,replace(replace('피해량: 주문력의 100% / 150% / 1200%\r\n보호막: 주문력의 200% / 250% / 2000%\r\n방어력: 85 / 120 / 400 + 방어력의 50%\r\n마법 저항력: 100 / 150 / 500 + 마법 저항력의 50%','\r',char(13)),'\n',char(10)),'3칸 내의 적을 도발하고 4초 동안 방어력과 마법 저항력을 얻습니다. 이 시간 동안 갈리오는 행동 불가 및 저지 불가 상태가 됩니다.  이후 사거리 내의 모든 적에게 마법 피해를 입히고, 사거리 내의 아군에게는 4초 동안 보호막을 부여합니다.  도발: 사거리 내에 있는 공격이 가능한 적들이 도발한 대상을 공격합니다.');
INSERT INTO champions_skill VALUES(6,'폭뢰','active',180,80,replace(replace('피해량: 주문력의 150% / 225% / 1000%\r\n기절 지속시간: 1.75 / 2 / 8','\r',char(13)),'\n',char(10)),'적이 가장 많은 일직선 방향으로 충격파를 3회 방출해 마법 피해를 입히고 적중하는 모든 적을 기절시킵니다.');
INSERT INTO champions_skill VALUES(7,'계산된 공격!','active',60,20,replace(replace('1차 피해량: 주문력의 220% / 330% / 530%\r\n\r\n2차 피해량: 주문력의 100% / 150% / 240%','\r',char(13)),'\n',char(10)),'기본 지속 효과: 처치 시 전투가 끝날 때까지 튕기는 횟수가 1회 증가합니다.  사용 시: 미사일을 발사하여 현재 대상에게 마법 피해를 입힙니다. 미사일은 2칸 내의 가장 멀리 있는 유닛에게 튕겨 마법 피해를 입힙니다.');
INSERT INTO champions_skill VALUES(8,'사냥 개시','active',50,0,replace(replace('공격력: 95% / 95% / 100%\r\n공격 속도: 주문력의 15% / 20% / 25%','\r',char(13)),'\n',char(10)),'공격력이 증가하고, 5초 동안 자신과 주변 아군의 공격 속도를 증가시킵니다.');
INSERT INTO champions_skill VALUES(9,'종이 가시','active',75,30,'피해량: 주문력의 90% / 135% / 210%','덩굴 채찍손 2마리를 소환하여 가장 가까운 적을 3회 공격하도록 합니다. 덩굴 채찍손의 공격은 마법 피해를 입히며 대상에게 6초 동안 상처 효과를 적용합니다.  상처: 체력 회복량이 33% 감소합니다.');
INSERT INTO champions_skill VALUES(10,'검무','passive',0,0,replace(replace('기본 지속 효과 피해량: 공격력의 150% / 150% / 700%\r\n사용 시 공격력 비례 피해량: 300% / 300% / 2000%\r\n사용 시 피해량: 사용 시 공격력 비례 피해량 + 주문력의 100%','\r',char(13)),'\n',char(10)),'기본 지속 효과: 기본 공격 시 적 사이를 오가는 칼날을 내보내 물리 피해를 입히고 5초 동안 30%의 파열을 적용합니다. 기본 공격 8회 사용할 때마다 이 스킬을 사용합니다.  사용 시: 칼날을 조종해 체력이 가장 낮은 적 3명을 빠르게 공격하여 물리 피해를 입히고 적중 시 효과를 적용합니다.  이렐리아가 추가 공격 속도의 25%를 전환하여 칼날의 속도를 높이고 공격력을 50% 증가시킵니다.  파열: 방어력 감소');
INSERT INTO champions_skill VALUES(11,'잡아, 티버!','active',140,70,replace(replace('피해량: 주문력의 180% / 270% / 1500%\r\n주문력 비례 추가 체력: 주문력의 325% / 400% / 2500%\r\n추가 체력: 체력의 15% + 주문력 비례 추가 체력\r\n기본 지속 회복량 = 체력의 5%','\r',char(13)),'\n',char(10)),replace(replace('기본 지속 효과: 불태우기가 적용된 적에게 피해를 주면 체력을 회복합니다.\r\n\r\n사용 시: 첫 사용 시 티버에게 올라타 최대 체력을 얻고 2칸 내의 적을 1.5초 동안 기절시킵니다.\r\n\r\n2칸 내의 적에게 마법 피해를 입히고 8초 동안 불태우기와 상처 효과를 적용합니다.\r\n\r\n불태우기: 매초 대상 최대 체력의 1%에 해당하는 고정 피해를 입힙니다\r\n상처: 체력 회복량이 33% 감소합니다.','\r',char(13)),'\n',char(10)));
INSERT INTO champions_skill VALUES(12,'이익!','active',50,0,replace(replace('피해량: 주문력의 200% / 300% / 900%\r\n소형 구체 피해량: 주문력의 100% / 150% / 450%\r\n대형 구체 피해량: 주문력의 300% / 450% / 1350%','\r',char(13)),'\n',char(10)),replace(replace('현재 대상 방향으로 구체를 던져 처음 부딪히는 적과 그 주변 적에게 마법 피해를 입힙니다. 이후 구체는 작아진 상태로 계속 굴러가 부딪히는 적에게 마법 피해를 입힙니다.\r\n\r\n세 번 사용할 때마다 대상에게 거대 구체를 던져 마법 피해를 입힙니다. 이후 거대 구체는 3개의 소형 구체로 분열되어 가장 멀리 있는 적 방향으로 굴러갑니다.','\r',char(13)),'\n',char(10)));
INSERT INTO champions_skill VALUES(13,'도약의 만개','active',80,40,replace(replace('피해량 감소: 30% / 30% / 35%\r\n체력 회복: 275 / 300 / 350 (주문력 비례)\r\n피해량: 주문력의 200% / 300% / 475%','\r',char(13)),'\n',char(10)),'공중으로 뛰어올라 받는 피해가 감소하고 2초에 걸쳐 체력을 회복합니다. 이후 땅을 내려찍어 주변 적에게 마법 피해를 입힙니다.');
INSERT INTO synergies_job VALUES(1,'거대 괴수',replace(replace('(2) 방어력 및 마법 저항력 +25\r\n\r\n(4) 방어력 및 마법 저항력 +45\r\n\r\n(6) 방어력 및 마법 저항력 +75','\r',char(13)),'\n',char(10)),'거대 괴수의 방어력과 마법 저항력이 증가합니다. 괴수가 사망할 때마다 가장 가까운 괴수가 5초 동안 해당 효과를 50% 추가로 얻습니다.');
INSERT INTO synergies_job VALUES(2,'파수꾼',replace(replace('(2) 피해 감소 15%\r\n\r\n(4) 피해 감소 25%\r\n\r\n(6) 피해 감소 40%','\r',char(13)),'\n',char(10)),'파수꾼이 받는 피해가 감소합니다. 전투 시작 직후 10초 동안 받는 피해가 12% 추가로 감소합니다.');
INSERT INTO synergies_job VALUES(3,'연인',replace(replace('(1) 연인이 전방 또는 후방 2열에 배치되는지에 따라 어떤 연인을 배치할지 변경합니다. 배치된 연인이 스킬을 사용하면 다른 연인은 추가 효과를 제공합니다.\r\n\r\n전방: 재주꾼 자야\r\n후방: 이타심 라칸','\r',char(13)),'\n',char(10)),'연인이 전방 또는 후방 2열에 배치되는지에 따라 어떤 연인을 배치할지 변경합니다. 배치된 연인이 스킬을 사용하면 다른 연인은 추가 효과를 제공합니다.');
INSERT INTO synergies_job VALUES(4,'이타심',replace(replace('(2) 방어력 및 마법 저항력 +10\r\n\r\n(3) 방어력 및 마법 저항력 +25\r\n\r\n(4) 방어력 및 마법 저항력 +40','\r',char(13)),'\n',char(10)),'이타심은 입힌 피해의 15%를 가장 체력이 낮은 아군의 체력으로 회복합니다. 아군의 방어력과 마법 저항력이 증가합니다.');
INSERT INTO synergies_job VALUES(5,'난동꾼',replace(replace('(2) 최대 체력 +20%\r\n\r\n(4) 최대 체력 +40%\r\n\r\n(6) 최대 체력 +65%\r\n\r\n(8) 최대 체력 +80%, 매 3초마다, 난동꾼이 다음 기본 공격 시 체력의 6%에 해당하는 추가 물리 피해를 입힙니다.','\r',char(13)),'\n',char(10)),'아군이 최대 체력을 100 얻습니다. 난동꾼은 추가 최대 체력을 얻습니다.');
INSERT INTO synergies_job VALUES(6,'비전 마법사',replace(replace('(2) 아군의 주문력 20 증가\r\n\r\n(4) 아군의 주문력 20 증가, 비전 마법사는 40 추가 증가\r\n\r\n(6) 아군의 주문력 40 증가, 비전 마법사는 80 추가 증가\r\n\r\n(8) 아군의 주문력 100 증가','\r',char(13)),'\n',char(10)),'비전 마법사가 주문력을 얻고 아군에게 주문력을 부여합니다.');
INSERT INTO synergies_job VALUES(7,'재주꾼',replace(replace('(2) 1회 튕김, 이전 피해량의 40%\r\n\r\n(4) 2회 튕김, 이전 피해량의 60%','\r',char(13)),'\n',char(10)),'재주꾼의 스킬이 튕깁니다. 튕길 때마다 이전에 입힌 피해량의 일정 비율만큼 피해를 입힙니다.');
INSERT INTO synergies_job VALUES(8,'현자',replace(replace('(2) 모든 피해 흡혈 +12%, 주문력 +15\r\n\r\n(3) 모든 피해 흡혈 +20%, 주문력 +30\r\n\r\n(4) 모든 피해 흡혈 +30%, 주문력 +45\r\n\r\n(5) 모든 피해 흡혈 +45%, 주문력 +70','\r',char(13)),'\n',char(10)),'전투 시작: 전방 2열의 아군이 모든 피해 흡혈을 얻습니다. 후방 2열의 아군이 주문력을 얻습니다.');
INSERT INTO synergies_job VALUES(9,'결투가',replace(replace('(2) 중첩당 공격 속도 +5%\r\n\r\n(4) 중첩당 공격 속도 +9%\r\n\r\n(6) 중첩당 공격 속도 +14%; 결투가가 받는 피해 15% 감소\r\n\r\n(8) 중첩당 공격 속도 +22%; 결투가가 받는 피해 24% 감소','\r',char(13)),'\n',char(10)),'결투가는 공격할 때마다 공격 속도를 얻습니다. 해당 효과는 12번까지 중첩됩니다.');
INSERT INTO synergies_job VALUES(10,'기원자',replace(replace('(2) 모든 아군이 5의 마나 회복\r\n\r\n(4) 기원자는 20 마나 회복, 다른 아군은 5 마나 회복\r\n\r\n(6) 기원자는 45 마나 회복, 다른 아군은 20 마나 회복','\r',char(13)),'\n',char(10)),'3초마다 아군 유닛의 마나가 회복됩니다.');
INSERT INTO synergies_origin VALUES(1,'숲지기',replace(replace('(2) +10 주문력, 적 사망당 체력 2 획득\r\n\r\n(4) +30 주문력, 적 사망당 체력 5 획득\r\n\r\n(6) +60 주문력, 적 사망당 체력 9 획득','\r',char(13)),'\n',char(10)),'숲지기가 주문력과 100의 체력을 얻습니다. 적이 사망할 때마다 추가 체력을 얻습니다.');
INSERT INTO synergies_origin VALUES(2,'이야기꾼',replace(replace('(3) 지원 효과를 선택합니다. 체력 +60 획득.\r\n\r\n(5) 전투 효과를 선택합니다. 체력 +100 획득.\r\n\r\n(7) 전투 효과를 선택합니다. 체력 +150 획득.\r\n\r\n(10) 초월합니다. 체력 +250 획득.','\r',char(13)),'\n',char(10)),'이야기꾼은 케일이라는 이름의 영웅을 소환해 진화시킵니다. 이야기꾼이 최대 체력을 얻습니다.  이야기꾼의 등급마다 케일의 체력과 주문력이 증가합니다. 케일은 게임 스테이지마다 10%의 공격 속도를 얻습니다.');
INSERT INTO synergies_origin VALUES(3,'용군주',replace(replace('(2) 피해량 5%, 공격속도 20% 증가.\r\n\r\n(3) 피해량 10%, 공격속도 30% 증가.\r\n\r\n(4) 피해량 12% 및 1.5초 동안 기절.\r\n\r\n(5) 피해량 18%, 공격속도 40% 증가.','\r',char(13)),'\n',char(10)),'8초 이상 전투를 벌이면 용이 전장을 공격해 적에게 고정 피해를 입히고 전투가 끝날 때까지 모든 아군에게 공격 속도를 부여합니다.');
INSERT INTO synergies_origin VALUES(4,'신화',replace(replace('(3) 체력 +10%, 주문력, 공격력 +12%\r\n\r\n(5) 체력 +16%, 주문력, 공격력 +20%\r\n\r\n(7) 체력 +24%, 주문력, 공격력 +32%\r\n\r\n(10) 즉시 서사가 됩니다. 보너스가 대신 250% 증가합니다.','\r',char(13)),'\n',char(10)),'신화 챔피언이 체력, 주문력, 공격력을 얻습니다.  플레이어 전투 4번 후 서사가 되어 보너스가 50% 증가합니다.');
INSERT INTO synergies_origin VALUES(5,'행운',replace(replace('(3) 주사위를 굴립니다. 그 횟수만큼의 플레이어 대상 전투에서 행운을 보상으로 변환할 수 있는 축제를 엽니다.\r\n\r\n(5) 플레이어 대상 전투가 시작될 때마다 플레이어 체력을 3 회복합니다.\r\n\r\n(7) 운명이 미소를 짓습니다! 턴마다 추가 보상과 더 많은 행운을 무조건 획득하고 축제를 엽니다.','\r',char(13)),'\n',char(10)),'전투에서 패배하면 행운을 얻습니다. 연이어 패배하면 더 많은 행운을 누적합니다.  승리하면 행운을 잃습니다.');
INSERT INTO synergies_origin VALUES(6,'천계',replace(replace('(2) 100% 증가\r\n\r\n(3) 115% 증가\r\n\r\n(4) 135% 증가\r\n\r\n(5) 165% 증가\r\n\r\n(6) 200% 증가\r\n\r\n(7) 240% 증가','\r',char(13)),'\n',char(10)),replace(replace('천계 챔피언은 아군에게 등급과 전장에 있는 천계 유닛의 수에 따라 증가하는 고유한 능력치를 부여합니다.\r\n\r\n카직스 - 치명타 확률 +15%\r\n말파이트 - 방어력 및 마법 저항력 +8\r\n니코 - 체력 +60\r\n키아나 - 공격력 +10%\r\n소라카 - 주문력 +10%\r\n오공 - 공격속도 +12%\r\n상징 - 모든 피해 흡혈 +3%\r\n\r\n2성 유닛은 30% 추가로 얻습니다.\r\n3성 유닛은 50% 추가로 얻습니다.','\r',char(13)),'\n',char(10)));
